var pickedColor,squares=document.querySelectorAll(".colors__item"),colorDisplay=document.querySelector(".main__color-display"),scoreDisplay=document.querySelector(".main__score-keeper"),pageHeader=document.querySelector(".page-header"),gameOver=document.querySelector(".game-over"),playAgain=document.querySelector("#play-again"),scoreCounter=0,colors=doColors(6);function startNewGame(){scoreCounter=0,scoreDisplay.textContent="Score: 0",pageHeader.style.background="#354984",colors=doColors(6),addColorsToSquares(),changePickedColor()}function randomInteger(o,e){var r=o-.5+Math.random()*(e-o+1);return r=Math.round(r)}function randomColor(){return"rgb("+randomInteger(0,255)+", "+randomInteger(0,255)+", "+randomInteger(0,255)+")"}function doColors(o){for(var e=[],r=0;r<o;r++)e.push(randomColor());return e}function changePickedColor(){pickedColor=Math.floor(Math.random()*colors.length),colorDisplay.textContent=colors[pickedColor]}function addColorsToSquares(){for(var o=0;o<squares.length;o++)squares[o].style.background=colors[o],squares[o].style.transition="1s"}addColorsToSquares(),changePickedColor();for(var i=0;i<squares.length;i++)squares[i].addEventListener("click",function(){this.style.background==colors[pickedColor]?(pageHeader.style.background=colors[pickedColor],scoreCounter++,scoreDisplay.textContent="Score: "+scoreCounter,colors=doColors(6),addColorsToSquares(),changePickedColor()):(gameOver.classList.add("show-message"),colors[pickedColor]=!1)});playAgain.addEventListener("click",function(){startNewGame(),gameOver.classList.remove("show-message")});